{
  "outcome_table": [
    {
      "sl": ">= +6",
      "result": "Astounding Success",
      "description": "Yes, perfectly!: The result is as good as it can be, perhaps with extra luck and fortunate coincidences thrown in!"
    },
    {
      "sl": "+4 to +5",
      "result": "Impressive Success",
      "description": "Yes, and...: You achieve your goal with style, exceeding your expectations."
    },
    {
      "sl": "+2 to +3",
      "result": "Success",
      "description": "Yes: You achieve a solid success."
    },
    {
      "sl": "+0 to +1",
      "result": "Marginal Success",
      "description": "Yes, but...: You more or less achieve what you intend, but imperfectly, and perhaps with an unpredictable side effect."
    },
    {
      "sl": "-1 to -0",
      "result": "Marginal Failure",
      "description": "No, but...: You marginally fail, perhaps accomplishing a portion of what you intended."
    },
    {
      "sl": "-2 to -3",
      "result": "Failure",
      "description": "No: You just plain do it wrong."
    },
    {
      "sl": "-4 to -5",
      "result": "Impressive Failure",
      "description": "No, and...: Not only do you mess up, but you also cause additional things to go wrong."
    },
    {
      "sl": "<= -6",
      "result": "Astounding Failure",
      "description": "No, not in any way!: Everything goes wrong in the worst possible way. The GM will likely add to your woes with unanticipated consequences of your actions. Surely no-one is this unlucky; you have clearly offended the gods."
    }
  ]
}

