9.content:
{
  "meta_currency_uses": {
    "fortune": {
      "name": "Fortune Points",
      "description": "A pool of points representing minor twists of luck, refilled each session.",
      "spend_options": [
        {
          "name": "Reroll a Test",
          "effect": "You may spend one Fortune point to reroll a failed Test."
        },
        {
          "name": "Add +1 SL",
          "effect": "You may spend one Fortune point to add +1 SL to a Test *after* it has been rolled."
        },
        {
          "name": "Seize the Initiative",
          "effect": "At the start of a Round, you may spend one Fortune point to choose when you act, ignoring Initiative order for that Round only."
        }
      ]
    },
    "fate": {
      "name": "Fate Points",
      "description": "A permanent resource representing your character's destiny, used to avoid death.",
      "spend_options": [
        {
          "name": "Die Another Day",
          "effect": "Instead of dying, you are knocked out, left for dead, or otherwise removed from the encounter. You survive but take no further part in the current scene."
        },
        {
          "name": "How Did That Miss?",
          "effect": "You completely avoid all damage from a single source through an extraordinary fluke. You remain in the fight."
        }
      ]
    },
    "resolve": {
      "name": "Resolve Points",
      "description": "A pool of points representing your ability to push through minor obstacles.",
      "spend_options": [
        {
          "name": "Become Immune to Psychology",
          "effect": "You may spend one Resolve point to become immune to Psychology until the end of the next Round."
        },
        {
          "name": "Ignore Critical Wounds",
          "effect": "You may spend one Resolve point to ignore all modifiers from all Critical Wounds until the start of the next Round."
        },
        {
          "name": "Remove a Condition",
          "effect": "You may spend one Resolve point to remove one Condition. If you remove the Prone Condition this way, you also regain 1 Wound."
        }
      ]
    },
    "resilience": {
      "name": "Resilience Points",
      "description": "A permanent resource representing your character's inner drive and determination.",
      "spend_options": [
        {
          "name": "I Deny You!",
          "effect": "Choose not to develop a mutation you would otherwise receive from Corruption. You do not lose any Corruption points."
        },
        {
          "name": "I Will Not Fail!",
          "effect": "Instead of rolling a Test, you automatically succeed. In an Opposed Test, you win by +1 SL. You can even use this on a Test that has already been failed. If this causes a Critical, you may choose the Hit Location."
        }
      ]
    }
  }
}

